INSERT INTO `hb_news` (`id`, `class`, `type`, `title`, `pic`, `inf`, `addtime`, `username`, `newsdate`, `sort_order`) VALUES
(1, '关于我们', 'aboutus', '关于豪邦', 'gongsijianjie.jpg', '<p style="text-indent: 2em; text-align: justify;"><span style="color: rgb(117, 117, 117); font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="color: rgb(85, 85, 85); font-family: 宋体; font-size: 14px; line-height: 21px; text-align: justify; text-indent: 32px; white-space: normal; background-color: rgb(255, 255, 255);">安徽豪邦保安服务有限公司是２０１５年经安徽省公安厅批准，取得保安许可资质的社会化重要安保服务企业。注册资金100万元。公司服务范围涵盖人防、技防、安全咨询、消防培训等，主要为企事业单位、政府机关、铁路、车站、公园、大型商场、星级酒店、高端小区、高校等大型场所及重大活动提供安全防范服务和随身护卫工作。</span></span></p><p style="text-indent: 2em; text-align: justify;"><span style="color: rgb(117, 117, 117); font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; background-color: rgb(255, 255, 255);"><br/></span></p><p style="text-indent: 2em; text-align: justify;"><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(117, 117, 117); font-family: Simsun; font-size: 12px; white-space: normal; line-height: 1.75em; text-indent: 2em; text-align: justify; background-color: rgb(255, 255, 255);"><br/></p>', '2015-06-03 10:21:16', '', '', 0),
(2, '关于我们', 'aboutus', '公司简介', '', '<p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">&nbsp; &nbsp; 安徽豪邦保安服务有限公司是２０１５年经安徽省公安厅批准，取得保安许可资质的社会化重要安保服务企业。注册资金100万元。公司服务范围涵盖人防、技防、安全咨询、消防培训等，主要为企事业单位、政府机关、铁路、车站、公园、大型商场、星级酒店、高端小区、高校等大型场所及重大活动提供安全防范服务和随身护卫工作。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">公司坚持“</span><span style="color: red; font-family: 宋体; background: white;">铸安全盾牌，立服务口碑</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">”。秉承“</span><strong><span style="color: red; font-family: 宋体; background: white;">诚信为本，客户至上，安全第一，优质服务</span></strong><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">”的宗旨。“树杰出保安品牌，立恒久服务企业”是豪邦公司的远景规划和宏伟目标。</span><span style="color: red; font-family: 宋体; background: white;">力争在短时间内成为一个规模较大、科技含量较高、服务优良、实力雄厚的专业保安服务企业</span><span style="font-family: 宋体; background: white;">。</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">为客户人身财产安全提供坚实保障。协助公安机关维护社会治安发挥积极有效的作用，我们将用全新、快捷、周到、细致、真诚地服务不断创造出合作双方美好未来的新天地。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-family: 宋体; background: white;">我公司将对保安队伍实行准军事化管理．建立一支具有专业安保知识、训练有素的保安队伍，服务客户。还将建一支形象好、素质高、受过特种训练的特保队，为需要押运护卫或人身安全护卫提供可靠的安保服务。特保部有精良的交通、通讯装备，公司热忱欢迎有志之士加入我们团队。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">&nbsp;&nbsp;&nbsp; 经营理念：</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体;">&nbsp;&nbsp;&nbsp; １、</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">企业管理理念：专业化、军事化、集团化、智能化。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体;">&nbsp;&nbsp;&nbsp; ２、</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">客户服务理念：保安我们专业，服务我们更好。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体;">&nbsp;&nbsp;&nbsp; ３、</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">企业沟通理念：简单、直接，高效。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体;">&nbsp;&nbsp;&nbsp; ４、</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">团队合作理念：雁一般的团队，鹰一般的个人。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体;">&nbsp;&nbsp;&nbsp; ５、</span><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">我们的经营方针：基于做大程度地满足客户要求，同时保证企业效益。</span></p><p style="white-space: normal;"><span style="color: rgb(85, 85, 85); font-family: 宋体; background: white;">　　　　客户为先，多样化、快速、简便、安全地为客户服务</span></p><p style="margin-top: 10px; margin-bottom: 0px; white-space: normal; padding: 0px; font-size: 12px; color: rgb(117, 117, 117); text-indent: 2em; line-height: 1.75em; font-family: Simsun; text-align: justify; background-color: rgb(255, 255, 255);"><span style="padding: 0px; font-size: 14px; margin: 0px;"></span></p><p style="margin-top: 10px; margin-bottom: 0px; white-space: normal; padding: 0px; font-size: 12px; color: rgb(117, 117, 117); line-height: 1.75em; font-family: Simsun; background-color: rgb(255, 255, 255);"><span style="padding: 0px; font-size: 14px; margin: 0px;">&nbsp; &nbsp;&nbsp;</span></p><p><br/></p>', '2015-06-03 09:26:09', '', '', 0),
(3, '关于我们', 'aboutus', '企业资质', '', '<p><img title="1433905505118165.jpg" src="/ueditor/php/upload/image/20150610/1433905505118165.jpg"/></p><p><img title="1433905511214083.jpg" src="/ueditor/php/upload/image/20150610/1433905511214083.jpg"/></p><p><img title="1433905516515477.jpg" src="/ueditor/php/upload/image/20150610/1433905516515477.jpg"/></p><p style="TEXT-ALIGN: center"><img title="1433325972268260.jpg" src="/ueditor/php/upload/image/20150603/1433325972268260.jpg"/></p><p><br/></p><p style="TEXT-ALIGN: center"><img title="1433325972511820.jpg" src="/ueditor/php/upload/image/20150603/1433325972511820.jpg"/></p><p style="WHITE-SPACE: normal"><br/></p><p style="WHITE-SPACE: normal; TEXT-ALIGN: center"><br/></p><p><br/></p>', '2015-06-10 03:05:30', '', '', 0),
