<?php/** * Copy Right IJH.CC * Each engineer has a duty to keep the code elegant * $Id$ */if(!defined('__CORE_DIR')){    exit("Access Denied");}class Ctl_Xiaoqu_News extends Ctl{        public function index($page=1)    {        $filter = $pager = array();        $pager['page'] = max(intval($page), 1);        $pager['limit'] = $limit = 50;                if($SO = $this->GP('SO')){            $pager['SO'] = $SO;            if($SO['news_id']){$filter['news_id'] = $SO['news_id'];}if($SO['xiaoqu_id']){$filter['xiaoqu_id'] = $SO['xiaoqu_id'];}if($SO['from']){$filter['from'] = $SO['from'];}if($SO['title']){$filter['title'] = "LIKE:%".$SO['title']."%";}if(is_array($SO['dateline'])){if($SO['dateline'][0] && $SO['dateline'][1]){$a = strtotime($SO['dateline'][0]); $b = strtotime($SO['dateline'][1])+86400;$filter['dateline'] = $a."~".$b;}}        }        $filter['closed'] = 0;        if($items = K::M('xiaoqu/news')->items($filter, null, $page, $limit, $count)){            $pager['count'] = $count;            $pager['pagebar'] = $this->mkpage($count, $limit, $page, $this->mklink(null, array('{page}')), array('SO'=>$SO));            $xiaoqu_ids = array();            foreach($items as $k=>$v){                $xiaoqu_ids[$v['xiaoqu_id']] = $v['xiaoqu_id'];            }            $xiaoqu_list = K::M('xiaoqu/xiaoqu')->items_by_ids($xiaoqu_ids);            $this->pagedata['xiaoqu_list'] = $xiaoqu_list;        }        $this->pagedata['items'] = $items;        $this->pagedata['pager'] = $pager;        $this->tmpl = 'admin:xiaoqu/news/items.html';    }    public function so()    {        $this->tmpl = 'admin:xiaoqu/news/so.html';    }    public function xiaoqu($xiaoqu_id, $page=1)    {        if(!$xiaoqu_id = (int)$xiaoqu_id){            $this->msgbox->add('参数错误', 211);        }else if(!$xiaoqu = K::M('xiaoqu/xiaoqu')->detail($xiaoqu_id)){            $this->msgbox->add('小区不存在或已经删除', 212);        }else{            $filter = $pager = array();            $pager['page'] = max(intval($page), 1);            $pager['limit'] = $limit = 50;            $filter['xiaoqu_id'] = $xiaoqu_id;            if($items = K::M('xiaoqu/news')->items($filter, null, $page, $limit, $count)){                $pager['count'] = $count;                $pager['pagebar'] = $this->mkpage($count, $limit, $page, $this->mklink(null, array('{page}')), array('SO'=>$SO));                $xiaoqu_ids = array();                foreach($items as $k=>$v){                    $xiaoqu_ids[$v['xiaoqu_id']] = $v['xiaoqu_id'];                }                $xiaoqu_list = K::M('xiaoqu/xiaoqu')->items_by_ids($xiaoqu_ids);                $this->pagedata['xiaoqu_list'] = $xiaoqu_list;            }            $this->pagedata['items'] = $items;            $this->pagedata['pager'] = $pager;            $this->pagedata['xiaoqu'] = $xiaoqu;            $this->tmpl = 'admin:xiaoqu/news/items.html';        }    }    public function detail($news_id = null)    {        if(!$news_id = (int)$news_id){            $this->msgbox->add('未指定要查看内容的ID', 211);        }else if(!$detail = K::M('xiaoqu/news')->detail($news_id)){            $this->msgbox->add('您要查看的内容不存在或已经删除', 212);        }else{            $this->pagedata['detail'] = $detail;            $this->tmpl = 'admin:xiaoqu/news/detail.html';        }    }    public function create($xiaoqu_id)    {        if(!($xiaoqu_id = (int)$xiaoqu_id) && !($xiaoqu_id = (int)$this->GP('xiaoqu_id'))){            $this->msgbox->add('未指定小区', 211);        }else if(!$xiaoqu = K::M('xiaoqu/xiaoqu')->detail($xiaoqu_id)){            $this->msgbox->add('小区不存在或已经删除', 211);        }else if($data = $this->checksubmit('data')){            if($_FILES['data']){                                foreach($_FILES['data'] as $k=>$v){                    foreach($v as $kk=>$vv){                        $attachs[$kk][$k] = $vv;                    }                }                $upload = K::M('magic/upload');                foreach($attachs as $k=>$attach){                    if($attach['error'] == UPLOAD_ERR_OK){                        if($a = $upload->upload($attach, 'xiaoqu')){                            $data[$k] = $a['photo'];                        }                    }                }            }            $data['xiaoqu_id'] = $xiaoqu_id;            if($news_id = K::M('xiaoqu/news')->create($data)){                $this->msgbox->add('添加内容成功');                $this->msgbox->set_data('forward', '?xiaoqu/news-xiaoqu-'.$xiaoqu_id.'.html');            }         }else{            $this->pagedata['xiaoqu'] = $xiaoqu;            $this->tmpl = 'admin:xiaoqu/news/create.html';        }    }    public function edit($news_id=null)    {        if(!($news_id = (int)$news_id) && !($news_id = $this->GP('news_id'))){            $this->msgbox->add('未指定要修改的内容ID', 211);        }else if(!$detail = K::M('xiaoqu/news')->detail($news_id)){            $this->msgbox->add('您要修改的内容不存在或已经删除', 212);        }else if($data = $this->checksubmit('data')){            if($_FILES['data']){                foreach($_FILES['data'] as $k=>$v){                    foreach($v as $kk=>$vv){                        $attachs[$kk][$k] = $vv;                    }                }                $upload = K::M('magic/upload');                foreach($attachs as $k=>$attach){                    if($attach['error'] == UPLOAD_ERR_OK){                        if($a = $upload->upload($attach, 'xiaoqu')){                            $data[$k] = $a['photo'];                        }                    }                }            }            if(K::M('xiaoqu/news')->update($news_id, $data)){                $this->msgbox->add('修改内容成功');            }          }else{            $this->pagedata['detail'] = $detail;            $this->pagedata['xiaoqu'] = K::M('xiaoqu/xiaoqu')->detail($detail['xiaoqu_id']);            $this->tmpl = 'admin:xiaoqu/news/edit.html';        }    }    public function doaudit($news_id=null)    {        if($news_id = (int)$news_id){            if(K::M('xiaoqu/news')->batch($news_id, array('audit'=>1))){                $this->msgbox->add('审核内容成功');            }        }else if($ids = $this->GP('news_id')){            if(K::M('xiaoqu/news')->batch($ids, array('audit'=>1))){                $this->msgbox->add('批量审核内容成功');            }        }else{            $this->msgbox->add('未指定要审核的内容', 401);        }    }    public function delete($news_id=null)    {        if($news_id = (int)$news_id){            if(!$detail = K::M('xiaoqu/news')->detail($news_id)){                $this->msgbox->add('你要删除的内容不存在或已经删除', 211);            }else{                if(K::M('xiaoqu/news')->delete($news_id)){                    $this->msgbox->add('删除内容成功');                }            }        }else if($ids = $this->GP('news_id')){            if(K::M('xiaoqu/news')->delete($ids)){                $this->msgbox->add('批量删除内容成功');            }        }else{            $this->msgbox->add('未指定要删除的内容ID', 401);        }    }  }