(2, 1, 1, 'help', 'pageinvite', '活动说明', '', '活动说明--分享活动的活动说明。字数不要太多，别人也懒的看', 1, 0, 1, 0, 0, '', 0, 0, 50, 1, 0, 1451036021),
(3, 1, 1, 'help', 'helptest', '测试文章', '', 'helptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptesthelptest', 1, 0, 1, 0, 0, '', 0, 0, 50, 1, 0, 1452753252),
